import React, {Component} from 'react';
import {Media} from 'react-bootstrap';
import {Link} from 'react-router';

import Comments from "../Comments";

let imageEmpty = "data:image/svg+xml;base64,";

export default class ViewPost extends Component {
    constructor(props) {
        super(props);
    }

    render() {
        let img = null;
        if (this.props.img === null) {
            img = {
                src: imageEmpty,
                alt: ''
            };
        } else {
            img = this.props.img;
        }
        let comments = <Comments postId={this.props._id} user={this.props.user} authorization={this.props.authorization}/>;
        return <Media>
            <Media.Left className="media-top">
                <a>
                    <img className="media-object" src={img.src} alt={img.alt}/>
                </a>
            </Media.Left>
            <Media.Body>
                <h4 className="media-heading">{this.props.post.title}<br/><span className="label label-default">{this.props.createDateTime}</span>
                </h4>
                <p>{this.props.post.longText}</p>
                <p><b>Author:</b> <Link to={`/profile/${this.props.author._id}`}>{this.props.author.title}</Link></p>
                <p>
                    <b>Comments ({this.props.commentsCount})</b>
                </p>
                <hr/>
                {comments}
            </Media.Body>
        </Media>
    }
}